import { useSelector } from "react-redux";

import SectionTitle from "../components/SectionTitle";
import ProfileCard from "../components/ProfileCard";
import BookmarkedModuleCard from "../components/BookmarkedModuleCard";
import StudyPlanCard from "../components/StudyPlanCard";
import { User } from "../types";
import ReviewedModuleCard from "../components/ReviewedModuleCard";
import ModuleList from "../components/ModuleList";
import { Modal, Button, Form, Input, Divider } from 'antd';
import { MailOutlined, LockOutlined } from '@ant-design/icons';

import { useState } from 'react';

const Profile: React.FC = () => {

  const [visible, setVisible] = useState(false);

  const showModal = () => {
    setVisible(true);
  };

  const hideModal = () => {
    setVisible(false);
  };

  const onFormFinish = () => {
    // do something when the form has been finished.
  }


  const dummyProfile: User = {
    fullName: "Chester Sim",
    studyStatus: "Year 4 Undergraduate in NUS",
    studyCourse: "Computer Science",
    modulesReviewed: 3,
    modulesTaken: 10
  }

  return (
    <>
      <Button type="primary" onClick={showModal}>
        Open Modal with customized footer
      </Button>
      <Modal
        visible={visible}
        title={
          <div style={{ textAlign: "center" }}>
            LOGIN
            </div>
        }
        centered
        onCancel={hideModal}
        footer={null}>
        <Form
          name="normal_login"
          className="login-form"
          initialValues={{}}
          onFinish={onFormFinish}>
          <Form.Item
            name="username"
            rules={[{ required: true, message: 'Please enter an email address!' }]}
          >
            <Input prefix={<MailOutlined/>} placeholder="Email Address" />
          </Form.Item>
          <Form.Item
            name="password"
            rules={[{ required: true, message: 'Please enter a password!' }]}
          >
            <Input
              prefix={<LockOutlined/>}
              type="password"
              placeholder="Password"
            />
          </Form.Item>
          <Form.Item>
            <Button type="primary" htmlType="submit" style={styles.loginFormButton}>LOG IN</Button>
          </Form.Item>
        </Form>
        <Divider/>
        <div style = {{textAlign: center}}> 
          Dont have an account? Sign Up <a>here</a>
        </div>

      </Modal>


      <ProfileCard user={dummyProfile} />
      <BookmarkedModuleCard />
      <div style={styles.container}>
        <StudyPlanCard />
        <ReviewedModuleCard />
      </div>
      <SectionTitle title={`Modules you might be interested in`} />
      <ModuleList />



    </>
  );
};

const styles = {
  container: {
    display: "flex",
  },
  loginFormButton: {
    width: "100%",
    backgroundColor: "#2D538C",
  }
}
export default Profile;
