import { Modal, Button, Form, Input, Divider } from "antd";
import { MailOutlined, LockOutlined } from "@ant-design/icons";
import { User } from "../types";


import { message } from "antd";
import userApi from "../api/user";
import { useState, useEffect } from "react";


import FormModal from "./FormModal";
import FormModalItem from "./FormModalItem";
import { codeBlue } from "../styles/colors";
import { ModalState } from "../types";





interface EditProfileModalProps{
  user : User,
  toggleEditProfileModal : () => void,
}

const EditProfileModal: React.FC<EditProfileModalProps> = ({
  user,
  toggleEditProfileModal,
}) => {


  const [name, setName] = useState(user.fullName);
  const [yearOfStudy, setYearOfStudy] = useState(user.yearOfStudy);
  const [studyCourseName, setCourseName] = useState(user.studyCourse);


  const onFormFinish = () => {

    const newUserDetails : User = {
      fullName : name,
      yearOfStudy : yearOfStudy,
      studyCourse : studyCourseName,
      _id : user._id
    }


    userApi.updateUser(newUserDetails)
      .then((response) => {
        message.success(`Your profile has been updated successfully`);
      })
      .catch((error)=> {
        message.error(error);
      })
  };

  return (
    <Modal
        visible={true}
        title={<div style={{ textAlign: "center" }}> SIGNUP </div>}
        centered
        onCancel={toggleEditProfileModal}
        footer={null}
      >
        <Form
          name="normal_login"
          className="login-form"
          initialValues={{}}
          onFinish={onFormFinish}
        >
          <Form.Item
            name="fullname"
            rules={[
              { required: true, message: "Please enter your full name!" },
            ]}
          >
            <Input placeholder="Full Name" />
          </Form.Item>


        </Form>

    </Modal>
  );
};

const styles = {
  loginFormButton: {
    width: "100%",
    backgroundColor: "#2D538C",
  },
};

export default EditProfileModal;
