import { Modal, Button, Form, Input, Divider } from 'antd';
import { MailOutlined, LockOutlined } from '@ant-design/icons';
import { useState } from 'react';



const LoginModal: React.FC = ({toggleLoginModal}) => {
  const [visible, setVisible] = useState(true);

const showModal = () => {
  toggleLoginModal();
};

const hideModal = () => {
  toggleLoginModal();
};

const onFormFinish = () => {
  // do something when the form has been finished.
}

  return (
    <>
      <Modal
        visible={visible}
        title={
          <div style={{ textAlign: "center" }}>
            LOGIN
            </div>
        }
        centered
        onCancel={hideModal}
        footer={null}>
        <Form
          name="normal_login"
          className="login-form"
          initialValues={{}}
          onFinish={onFormFinish}>
          <Form.Item
            name="username"
            rules={[{ required: true, message: 'Please enter an email address!' }]}
          >
            <Input prefix={<MailOutlined />} placeholder="Email Address" />
          </Form.Item>
          <Form.Item
            name="password"
            rules={[{ required: true, message: 'Please enter a password!' }]}
          >
            <Input
              prefix={<LockOutlined />}
              type="password"
              placeholder="Password"
            />
          </Form.Item>
          <Form.Item>
            <Button type="primary" htmlType="submit" style={styles.loginFormButton}>LOG IN</Button>
          </Form.Item>
        </Form>
        <Divider />
        <div style={{ textAlign: "center" }}>
          Dont have an account? Sign Up <a> here</a>
        </div>

      </Modal>
    </>
  );


};

const styles = {
  loginFormButton: {
    width: "100%",
    backgroundColor: "#2D538C",
  }
}


export default LoginModal;
