import { MailOutlined, LockOutlined } from "@ant-design/icons";

import authService from "../utils/authentication";

import { message } from "antd";
import courseApi from "../api/course";
import { useState, useEffect } from "react";


import FormModal from "./FormModal";
import FormModalItem from "./FormModalItem";
import { codeBlue } from "../styles/colors";
import { ModalState } from "../types";





const AddCourseModal: React.FC<ModalState> = ({
  isModalVisible,
  setModalVisibility,
}) => {


  const [course, setCourse] = useState("");

  const onSubmit = () => {
    console.log(course);
    // courseApi.addCourse(addCourseValues.coursename);
  };

  return (
    <FormModal
      backgroundColor={codeBlue}
      submitColor="#3E59B9"
      title="Add Module"
      isModalVisible={isModalVisible}
      setModalVisibility={setModalVisibility}
      onSubmit={onSubmit}
    >
      <FormModalItem
        label="Module Code"
        type="input"
        value={course}
        setValue={setCourse}
      />
    </FormModal>
  );
};

const styles = {
  loginFormButton: {
    width: "100%",
    backgroundColor: "#2D538C",
  },
};

export default AddCourseModal;
