import { Button } from "antd";
import "../styles/antd.less";

import { useSelector } from "react-redux";
import { useRouter } from "next/router";

import { useEffect, useState } from 'react';
import LoginModal from "./LoginModal";
import SignupModal from "./SignupModal";
import { reviewBlue } from "../styles/colors";
import { User } from "../types";
import CustomButton from "./Button";
import { Menu, Dropdown } from 'antd';



const ProfileButton: React.FC = () => {
  const [loginModalVisible, setLoginModalVisible] = useState(false);
  const [signupModalVisible, setSignupModalVisible] = useState(false);
  const isLoggedIn: boolean = useSelector(state => state.auth.isLoggedIn);
  const user: User = useSelector(state => state.auth.user);

  const router = useRouter();


  useEffect(() => {
    isLoggedIn ? setLoginModalVisible(false) : null;
    // console.log("the user is ");
    // console.log(user);
  })

  const toggleLoginModal = () => {
    setLoginModalVisible(!loginModalVisible);
  }

  const toggleSignupModal = () => {
    setSignupModalVisible(!signupModalVisible);
  }

  const switchModals = () => {
    setSignupModalVisible(!signupModalVisible);
    setLoginModalVisible(!loginModalVisible);
  }

  const navigateToProfilePage = () => {
    router.push({
      pathname: "/profile",
    })
  }


  const menu = (
    <Menu>
      <Menu.Item>
        <a target="_blank" rel="noopener noreferrer" href="http://www.alipay.com/">
          1st menu item
        </a>
      </Menu.Item>
      <Menu.Item>
        <a target="_blank" rel="noopener noreferrer" href="http://www.taobao.com/">
          2nd menu item
        </a>
      </Menu.Item>
      <Menu.Item>
        <a target="_blank" rel="noopener noreferrer" href="http://www.tmall.com/">
          3rd menu item
        </a>
      </Menu.Item>
    </Menu>
  );

  return (
    <>
      {
        isLoggedIn
          ? 
          <Dropdown overlay={menu}>
            {/* <Button> Testing</Button> */}
            <Button style={styles.container}>
              {user.fullName}
            </Button>
          </Dropdown>
          : <Button type="primary" onClick={toggleLoginModal}>
            Log In
          </Button>
      }

      {loginModalVisible && !isLoggedIn ? <LoginModal toggles={{ toggleLoginModal, switchModals }} /> : null}
      {signupModalVisible ? <SignupModal toggles={{ toggleSignupModal, switchModals }} /> : null}
    </>

  );
};

const styles = {
  container: {
    display: "flex",
    alignItems: "center",
    margin: "0px 10px",
    padding: 10,
    backgroundColor: reviewBlue,
    color: "#fff",
    borderRadius: 8
  },
  userName: {
    alignItems: "flex-end",
    paddingLeft: "10px",
    margin: 0,
    fontSize: 15
  }
}

export default ProfileButton;

