import { useSelector } from "react-redux";

import SectionTitle from "../components/SectionTitle";
import ProfileCard from "../components/ProfileCard";
import BookmarkedModuleCard from "../components/BookmarkedModuleCard";
import StudyPlanCard from "../components/StudyPlanCard";
import { User } from "../types";
import ReviewedModuleCard from "../components/ReviewedModuleCard";
import ModuleList from "../components/ModuleList";
import { Modal, Button, Form, Input } from 'antd';
import { UserOutlined, LockOutlined } from '@ant-design/icons';



import { useState } from 'react';

const Profile: React.FC = () => {

  const [visible, setVisible] = useState(false);

  const showModal = () => {
    setVisible(true);
  };

  const hideModal = () => {
    setVisible(false);
  };

  const onFormFinish = () => {
    // do something when the form has been finished.
  }


  const dummyProfile: User = {
    fullName: "Chester Sim",
    studyStatus: "Year 4 Undergraduate in NUS",
    studyCourse: "Computer Science",
    modulesReviewed: 3,
    modulesTaken: 10
  }

  return (
    <>
      <Button type="primary" onClick={showModal}>
        Open Modal with customized footer
      </Button>
      <Modal
        visible={visible}
        title={
          <div style={{ textAlign: "center" }}>
            LOGIN
            </div>
        }
        centered
        onCancel={hideModal}
        footer={null}>
        <Form
          name="normal_login"
          className="login-form"
          initialValues={{ }}
          onFinish={onFormFinish}>
          <Form.Item
            name="username"
            rules={[{ required: true, message: 'Please input your Username!' }]}
          >
            <Input prefix={<UserOutlined className="site-form-item-icon" />} placeholder="Username" />
          </Form.Item>
          <Form.Item
            name="password"
            rules={[{ required: true, message: 'Please input your Password!' }]}
          >
            <Input
              prefix={<LockOutlined className="site-form-item-icon" />}
              type="password"
              placeholder="Password"
            />
          </Form.Item>




        </Form>

      </Modal>


      <ProfileCard user={dummyProfile} />
      <BookmarkedModuleCard />
      <div style={styles.container}>
        <StudyPlanCard />
        <ReviewedModuleCard />
      </div>
      <SectionTitle title={`Modules you might be interested in`} />
      <ModuleList />



    </>
  );
};

const styles = {
  container: {
    display: "flex",
  }
}
export default Profile;
