import { User } from "../types";
import { Button } from 'antd';
import { useDispatch, useSelector } from "react-redux";

import { UserOutlined } from '@ant-design/icons';
import { useEffect, useState } from "react";
import EditProfileModal from "./EditProfileModal";




interface ProfileCardProps {
  user: User
}

const ProfileCard: React.FC<ProfileCardProps> = ({ user }) => {
  // const { fullName, studyCourse, yearOfStudy } = user;
  const [editProfileModalVisible, setEditProfileModalVisible] = useState(false);
  const userInfo = useSelector((state) => state.auth.user);

  const toggleEditProfileModal = () => {
    setEditProfileModalVisible(!editProfileModalVisible);
    console.log(editProfileModalVisible);
  }


  return (
    user
      ? <div style={styles.userInfoContainer}> 
      <div style={styles.fullName}> {userInfo.fullName}</div>
      {userInfo.yearOfStudy 
        ? <div style={styles.study}> Year {userInfo.yearOfStudy} Student </div>
        : null}
      {userInfo.courseName
        ? <div style={styles.study}> {userInfo.courseName}</div>
        : null}
      <div style={styles.editProfileButton}>
      <Button onClick={toggleEditProfileModal}>
        Edit Profile
      </Button>
      </div>
      {
        editProfileModalVisible 
          ? <EditProfileModal toggleEditProfileModal={toggleEditProfileModal} user={user}/>
          : null 
      }

    </div> 

      : 
        <div style={styles.userInfoContainer}> 
          <div style={styles.fullName}> Please Log In First</div>
        </div>
  );
};

const styles = {
  userInfoContainer: {
    padding: 50,
    paddingBottom: 30
  },
  study: {
    fontStyle: "italic",
    textAlign: "center" as "center",
  },
  fullName: {
    justifyContent: "center",
    fontSize: 30,
    fontWeight: "bold" as "bold",
    textAlign: "center" as "center",
  },
  moduleInfo: {
    fontSize: 15,
    textAlign: "center" as "center",
  },
  editProfileButton: {
    marginTop: "20px",
    textAlign: "center" as "center",
  }
}

export default ProfileCard;
