import axios from "axios";
import queryString from "query-string";
import { serverDomain } from "../config";
const reviewBaseUrl = `${serverDomain}/api/review`;
const ratingBaseUrl = `${serverDomain}/api/rating`;

const getReviewsOfModule = async (modId, limit = 10, page = 1) => {
  const query = queryString.stringify({
    modId
  });
  const response = await axios.get(`${reviewBaseUrl}/?${query}`);
  return response.data;
};

const getReviewsOfUser = async userId => {
  const query = queryString.stringify({
    userId
  });
  const response = await axios.get(`${reviewBaseUrl}/?${query}`, {
    withCredentials: true
  });
  return response.data;
};

const addReviewOfModule = async body => {
  const response = await axios.post(reviewBaseUrl, body, {
    withCredentials: true
  });
  return response.status;
};

const updateReviewOfModule = async (body, reviewId) => {
  const response = await axios.put(`${reviewBaseUrl}/${reviewId}`, body, {
    withCredentials: true
  });
  return response.status;
};

const addRating = async (value, type, userId, subId, sub) => {
  const response = await axios.post(ratingBaseUrl, {
    type,
    userId,
    subId,
    value,
    sub
  }, {
    withCredentials: true
  });
  return response.status;
};

const getRating = async (subscriberType, subscriberId, userId, ratingType) => {
  const query = queryString.stringify({
    sub: subscriberType,
    subId: subscriberId,
    userId,
    type: ratingType
  });
  const response = await axios.get(`${ratingBaseUrl}/?${query}`);
  return response.data;
};

const updateRating = async (value, type, userId, subId, sub, ratingId) => {
  const response = await axios.put(`${ratingBaseUrl}/${ratingId}`, {
    sub,
    subId,
    userId,
    type,
    value
  }, {
    withCredentials: true
  });
  return response.data;
};

export default {
  getReviewsOfModule,
  getReviewsOfUser,
  addReviewOfModule,
  addRating,
  getRating,
  updateRating,
  updateReviewOfModule
};
