import { Modal, Button, Form, Input, Divider } from "antd";
import { MailOutlined, LockOutlined } from "@ant-design/icons";
import { User } from "../types";
import { useState, useEffect, Dispatch, SetStateAction } from "react";


import { message } from "antd";
import userApi from "../api/user";


import FormModal from "./FormModal";
import FormModalItem from "./FormModalItem";
import { codeBlue } from "../styles/colors";
import { ModalState } from "../types";





interface EditProfileModalProps{
  user : User,
  toggleEditProfileModal : () => void
}

const EditProfileModal: React.FC<EditProfileModalProps> = ({
  user,
  toggleEditProfileModal,
}) => {


  // const [name, setName] = useState(user.fullName);
  // const [yearOfStudy, setYearOfStudy] = useState(user.yearOfStudy);
  // const [newCourseName, setCourseName] = useState(user.courseName);
  // const [newSchoolName, setSchoolName] = useState(user.schoolName);




  const onFormFinish = (profileValues) => {

    const newUserDetails : User = {
      fullName : name,
      email: user.email,
      yearOfStudy : profileValues.newYearOfStudy,
      courseName : profileValues.newCourseName,
      schoolName : profileValues.newSchoolName,
      _id : user._id
    }


    userApi.updateUser(newUserDetails)
      .then((response) => {
        message.success(`Your profile has been updated successfully`);
      })
      .catch((error)=> {
        message.error(error);
      })
  };

  return (
    <Modal
        visible={true}
        title={<div style={{ textAlign: "center" }}> SIGNUP </div>}
        centered
        onCancel={toggleEditProfileModal}
        footer={null}
      >
        <Form
          name="normal_login"
          className="login-form"
          initialValues={{}}
          onFinish={onFormFinish}
        >
          <Form.Item
            name="fullname"
            label="Full Name"
            initialValue={user.fullName}
            rules={[
              { required: true, message: "Please enter your full name!" },
            ]}
          >
            <Input />
          </Form.Item>

          <Form.Item
            name="newYearOfStudy"
            label="Year of Study"
            initialValue={user.yearOfStudy}
          >
            <Input/>
          </Form.Item>
          <Form.Item
            name="newCourseName"
            label="Your Course"
            initialValue={user.courseName}
          >
            <Input/>
          </Form.Item>
          <Form.Item
            name="newSchoolName"
            label="Your University"
            initialValue={user.schoolName}
          >
            <Input/>
          </Form.Item>
          <Form.Item>
            <Button
              type="primary"
              htmlType="submit"
              style={styles.loginFormButton}
            >
              SAVE PROFILE
           </Button>
           </Form.Item>


        </Form>

    </Modal>
  );
};

const styles = {
  loginFormButton: {
    width: "100%",
    backgroundColor: "#2D538C",
  },
};

export default EditProfileModal;
