    window.localStorage.removeItem("ROTTENMODS_EMAIL");
    window.localStorage.removeItem("ROTTENMODS_PASSWORD");

    dispatch({
        type: LOG_USER_OUT,
    })
}

export default {
    signUp,
    logIn,
    logOut
}
