import { Dispatch, SetStateAction } from "react";
import Link from "next/link";
import { useRouter } from "next/router";
import { useDispatch, useSelector } from "react-redux";
import { SEARCH_TERM } from "../redux/constants";
import { triggerRequireLoginMessage } from "../utils/helpers";

import "../styles/antd.less";
import { Dropdown, Menu, Input } from "antd";
import { DownOutlined } from "@ant-design/icons";
import { codeBlue } from "../styles/colors";
import ProfileButton from "./ProfileButton";
import Button from "./Button";

const { Search } = Input;

interface NavBarProps {
  setAddModuleModalVisibility: Dispatch<SetStateAction<boolean>>;
}

const Navbar: React.FC<NavBarProps> = ({ setAddModuleModalVisibility }) => {
  const dispatch = useDispatch();
  const router = useRouter();
  const isLoggedIn = useSelector((state) => state.auth.isLoggedIn);

  const menu = (
    <Menu>
      <Menu.Item>NUS</Menu.Item>
      <Menu.Item>NTU</Menu.Item>
      <Menu.Item>SMU</Menu.Item>
    </Menu>
  );

  const submitSearch = (searchTerm) => {
    dispatch({ type: SEARCH_TERM, payload: searchTerm });
    router.push({
      pathname: "/search",
      query: { s: searchTerm },
    });
  };

  const toggleAddModModalVisibility = () => {
    if (!isLoggedIn) {
      triggerRequireLoginMessage();
    } else {
      setAddModuleModalVisibility(true);
    }
  };

  return (
    <div style={styles.container}>
      <Link href="/">
        <div style={styles.logo}>
          <img src="/RottenMods.png" alt="RottenMods Logo" height="30" />
        </div>
      </Link>

      <div style={styles.searchContainer}>
        <Search
          placeholder="Search for modules"
          onSearch={(value) => submitSearch(value)}
          style={styles.searchBar}
        />
      </div>

      <div style={styles.actionsContainer}>
        <Dropdown overlay={menu}>
          <div style={{ marginRight: 20 }}>
            <a style={{ color: "#595959", marginRight: 6, fontSize: 18 }}>
              NUS
            </a>
            <DownOutlined />
          </div>
        </Dropdown>
        <Button
          style={styles.addModuleButton}
          onClick={toggleAddModModalVisibility}
        >
          Add Module
        </Button>
        <ProfileButton />
      </div>
    </div>
  );
};

const styles = {
  loginFormButton: {
    width: "100%",
    backgroundColor: codeBlue,
  },
  container: {
    display: "flex",
    flexDirection: "row" as "row",
    width: "100%",
    height: "70px",
    padding: "30px 0px",
    justifyContent: "space-between",
    alignItems: "baseline",
    position: "sticky" as "sticky",
  },
  logo: {
    flex: 1,
    cursor: "pointer",
  },
  searchContainer: {
    flex: 1,
    display: "flex",
    justifyContent: "center",
  },
  searchBar: {
    width: 200,
  },
  actionsContainer: {
    display: "flex",
    flex: 1,
    justifyContent: "flex-end",
  },
  addModuleButton: {
    display: "flex",
    alignItems: "center",
    margin: "0px 10px",
    padding: 10,
    backgroundColor: codeBlue,
    color: "#fff",
    borderRadius: 8,
  },
};

export default Navbar;
