import { User } from "../types";
import Link from "next/link";
import { Avatar } from 'antd';
import { UserOutlined } from '@ant-design/icons';


import StarOutlinedIcon from "../icons/StarOutlinedIcon";
import { useReducer } from "react";

interface ProfileCardProps {
  user: User
}

const ProfileCard: React.FC<ProfileCardProps> = ({ user }) => {
  const { fullName, studyStatus, studyCourse, modulesReviewed, modulesTaken } = user;

  return (
    <div style={styles.tcontainer}>
      <div style={styles.displayPicture}>
        <Avatar size={100} icon={<UserOutlined />} />
      </div>    

      <div style={styles.profileInfoContainer}>
        <div style={styles.userInfoContainer}> 
          <div style={styles.fullName}> {fullName}</div>
          <div style={styles.study}> {studyStatus}</div>
          <div style={styles.study}> {studyCourse}</div>
        </div>
        <div style={styles.userModuleInfoContainer}> 
          <div style={styles.moduleInfo}>Modules Reviewed: {modulesReviewed}</div>
          <div style={styles.moduleInfo}>Modules Taken: {modulesTaken}</div>
        </div>
      </div>

    </div>
  );
};

const styles = {
  tcontainer: {
    padding: 30,
    backgroundColor: "#223370",
    margin: "15px 0px",
    color: "#fff",
    borderRadius: 15,
    boxShadow: "0px 8px 8px rgba(0, 0, 0, 0.25)",
    marginBottom: 15,
    display: "flex",
    alignContent: "flex-start",
    alignItems: "center",
  },
  profileInfoContainer: {
    paddingLeft: 30,
    display: "flex",

  },
  userModuleInfoContainer: {
    marginLeft: 200,
    marginRight: 30,
    paddingRight: 30,
    display: "flex",
    flexDirection: "column",
    justifyContent: "center"
  },
  study: {
    fontStyle: "italic"
  },
  fullName: {
    fontSize: 30,
    fontWeight: "bold" as "bold"
  },
  moduleInfo: {
    fontSize: 15,
    textAlign: "center"
  }


}

export default ProfileCard;
