import { NextPage } from "next";
import Link from "next/link";
import { useDispatch } from "react-redux";
import { SEARCH_TERM } from "../redux/constants";

import { Dropdown, Menu, Input } from "antd";
import { DownOutlined } from "@ant-design/icons";
import LoginButton from "../components/LoginButton";
import "../styles/antd.less";

import { Modal, Button, Form, Divider } from 'antd';
import { MailOutlined, LockOutlined, UserOutlined } from '@ant-design/icons';
import { useState } from 'react';



const { Search } = Input;

const Navbar: NextPage = () => {

  const [loginModalVisible, setLoginModalVisible] = useState(false);
  const [signupModalVisible, setSignupModalVisible] = useState(false);

  const onLoginFormFinish = () => {
    // do something when the form has been finished.
  }
  const showLoginModal = () => {
    setLoginModalVisible(true);
  };

  const hideLoginModal = () => {
    setLoginModalVisible(false);
  };

  const onSignupFormFinish = () => {
    // do something when the form has been finished.
  }
  const showSignupModal = () => {
    setSignupModalVisible(true);
  };

  const hideSignupModal = () => {
    setSignupModalVisible(false);
  };


  const dispatch = useDispatch();

  const menu = (
    <Menu>
      <Menu.Item>NUS</Menu.Item>
      <Menu.Item>NTU</Menu.Item>
      <Menu.Item>SMU</Menu.Item>
    </Menu>
  );

  const submitSearch = (value) => {
    // Submit search value to search API
    console.log("Search Bar Value:", value);
    dispatch({ type: SEARCH_TERM, payload: value });
  };

  return (
    <div
      style={{
        display: "flex",
        flexDirection: "row",
        width: "100%",
        height: "70px",
        padding: "30px 0px",
        justifyContent: "space-between",
        alignItems: "baseline",
        position: "sticky",
      }}
    >
      <Link href="/">
        <div style={{ flex: 1, cursor: "pointer" }}>
          <img src="/RottenMods.png" alt="RottenMods Logo" height="30" />
        </div>
      </Link>

      <div style={{ flex: 1, display: "flex", justifyContent: "center" }}>
        <Search
          placeholder="Search for modules"
          onSearch={(value) => submitSearch(value)}
          style={{ width: 200 }}
        />
      </div>

      <div
        style={{
          display: "flex",
          flex: 1,
          justifyContent: "flex-end",
        }}
      >
        <Dropdown overlay={menu}>
          <div style={{ marginRight: 20 }}>
            <a style={{ color: "#595959", marginRight: 6, fontSize: 18 }}>
              NUS
            </a>
            <DownOutlined />
          </div>
        </Dropdown>
        <LoginButton isAuthenticated={true} />
      </div>


      <Button type="text" onClick={showLoginModal}>
        Log In
      </Button>
      <Button type="primary" onClick={showSignupModal}>
        Sign Up
      </Button>


      <Modal
        visible={loginModalVisible}
        title={
          <div style={{ textAlign: "center" }}>
            LOGIN
            </div>
        }
        centered
        onCancel={hideLoginModal}
        footer={null}>
        <Form
          name="normal_login"
          className="login-form"
          initialValues={{}}
          onFinish={onLoginFormFinish}>
          <Form.Item
            name="username"
            rules={[{ required: true, message: 'Please enter an email address!' }]}
          >
            <Input prefix={<MailOutlined />} placeholder="Email Address" />
          </Form.Item>
          <Form.Item
            name="password"
            rules={[{ required: true, message: 'Please enter a password!' }]}
          >
            <Input
              prefix={<LockOutlined />}
              type="password"
              placeholder="Password"
            />
          </Form.Item>
          <Form.Item>
            <Button type="primary" htmlType="submit" style={styles.loginFormButton}>LOG IN</Button>
          </Form.Item>
        </Form>
        <Divider />
        <div style={{ textAlign: "center" }}>
          Dont have an account? Sign Up <a>here</a>
        </div>

      </Modal>

      <Modal
        visible={signupModalVisible}
        title={
          <div style={{ textAlign: "center" }}>
            SIGNUP
            </div>
        }
        centered
        onCancel={hideSignupModal}
        footer={null}>
        <Form
          name="normal_signup"
          className="signup-form"
          initialValues={{}}
          onFinish={onSignupFormFinish}>
          <Form.Item
            name="fullname"
            rules={[{ required: true, message: 'Please enter your full name!' }]}
          >
            <Input prefix={<UserOutlined />} placeholder="Full Name" />
          </Form.Item>
          <Form.Item
            name="emailaddress"
            rules={[{ required: true, message: 'Please enter an email address!' }]}
          >
            <Input
              prefix={<LockOutlined />} placeholder="Email Address" />
          </Form.Item>

          <Form.Item
            name="emailaddress"
            rules={[{ required: true, message: 'Please enter an email address!' }]}
          >
            <Input
              prefix={<LockOutlined />} placeholder="Password" />
          </Form.Item>
          <Form.Item
            name="password"
            rules={[{ required: true, message: 'Please enter a password!' }]}
          >
            <Input
              prefix={<LockOutlined />}
              type="password"
              placeholder="Password"
            />
          </Form.Item>

          <Form.Item style={{ marginBottom: 0 }}>
            <Form.Item
              name="year"
              rules={[{ required: true }]}
              style={{ display: 'inline-block', width: 'calc(50% - 8px)' }}
            >
              <Input placeholder="Input birth year" />
            </Form.Item>
            <Form.Item
              name="month"
              rules={[{ required: true }]}
              style={{ display: 'inline-block', width: 'calc(50% - 8px)', margin: '0 8px' }}
            >
              <Input placeholder="Input birth month" />
            </Form.Item>
          </Form.Item>




        </Form>
        <Divider />
        <div style={{ textAlign: "center" }}>
          Dont have an account? Sign Up <a>here</a>
        </div>

      </Modal>




    </div>

  );
};


const styles = {
  loginFormButton: {
    width: "100%",
    backgroundColor: "#2D538C",
  }
}

export default Navbar;
