import axios from "axios";
import queryString from "query-string";
import { serverDomain } from "../config";
const baseUrl = `${serverDomain}/api/reaction`;

const addReaction = async (sub, subId, userId, type) => {
  await axios.post(baseUrl, {
    sub,
    subId,
    userId,
    type
  }, {
    withCredentials: true
  });
};

const getReaction = async (sub, subId, userId, type) => {
  const query = queryString.stringify({
    sub,
    subId,
    userId,
    type
  });
  const response = await axios.get(`${baseUrl}/?${query}`);
  return response.data;
};

const deleteReaction = async reactionId => {
  await axios.delete(`${baseUrl}/${reactionId}`);
};

export default {
  addReaction,
  getReaction,
  deleteReaction
};
